import React, { useEffect, useState } from "react";
import { items } from "../UI/data/Items";
import classes from './Cart.module.css'


const Cart = props => {

    const [cartitems, setItems] = useState([]);

    useEffect(() => {
        setItems([...items, props.cartItems])
    }, [props.cartItems]);
    // console.log(items);

    let [first, ...cartItems] = items
    console.log(cartItems);
    
    let [totalPrice, setTotalPrice] = useState(parseInt('0'));
    useEffect(()=>{
        setTotalPrice( totalPrice += props.cartItems.price)
    }, [props.cartItems])


    // console.log(totalPrice);
    

    return (<>
        <h1>CART</h1>
        <ul className={classes.cartList}>
            {items.map(item => {
                return (<li className={classes.listNodes} key={Math.random()}>
                    <div className={classes.itemName}>{item.name}</div>

                    <div className={classes.itemDetails}>
                        <div>{item.price}</div>
                        <div>{item.Quantity}  {item.measurment}</div>
                    </div>

                </li>)
            })
            }
        </ul>

        <div className={classes.totalPrice}>
            <h1>Total Price</h1>
            <h3>{totalPrice}</h3>
        </div>
    </>
    )

}

export default Cart;
