import React, { useEffect, useState } from "react";
import { items } from "../UI/data/Items";
import classes from './'


const Cart = props => {

    const [items, setItems] = useState([]);
    useEffect(() => {
        setItems([...items, props.cartItems])
    }, [props.cartItems]);

    console.log(items.slice(1));

    return (<div>
        <ul>
            {items.map(item => {
                return (<li key={Math.random()}>
                    {item.name} {item.price} {item.Quantity}-{item.measurment}
                </li>)
            })
            }
        </ul>
    </div>
    )

}

export default Cart;
