import React, { useEffect, useState } from "react";
import { items } from "../UI/data/Items";
import classes from './Cart.module.css'


const Cart = props => {

    // const [items, setItems] = useState([]);
    const [totalPrice, setTotalPrice] = useState(0);
    console.log(items);

    // useEffect(() => {
    //     setItems([...items, props.cartItems])
    // }, [props.cartItems]);
    // console.log(items);

    let [first, ...cartItems] = items
    console.log('cartItems', cartItems);

    useEffect(() => {
        if (cartItems[0] == null) {
            console.log('if ');
        }
        else {
            setTotalPrice(totalPrice + props?.cartItems?.price)
        }

    }, [props.cartItems])

    console.log('props',props.cartItems);


    //     let cartItemPrice = 0;
    //   cartItems.map(cartItemPrice =>{
    //     return cartItemPrice += cartItemPrice.price;
    //   })
    //   console.log(cartItemPrice);




    return (<>
        <h1>CART</h1>
        <ul className={classes.cartList}>
            {cartItems.map(cartItem => {
                return (<li className={classes.listNodes} key={Math.random()}>
                    <div className={classes.cartImage}>
                        <img src={cartItem.imageURL}></img>
                    </div>
                    <div>
                        <h2 className={classes.itemName}>{cartItem.name}</h2>

                        <div className={classes.itemDetails}>
                            <div>{cartItem.price}/-</div>
                            <div>{cartItem.Quantity}  {cartItem.measurment}</div>
                        </div>
                    </div>

                </li>)
            })
            }
        </ul>

        <div className={classes.totalPrice}>
            <h1>Total Price</h1>
            <h3>{totalPrice}</h3>
        </div>
    </>
    )

}

export default Cart;
