const Button = props => {

    setCartItems ({cartItems : props})

     const addToCartHandler = (e) => {
        props.setCartItems = "2";
        console.log(props.cartItems)
        console.log(props.id);

    }

    return (
        <button onClick={addToCartHandler} >Add to Cart</button>
    )
}

