import React, { useEffect, useState } from "react";
import { items } from "../UI/data/Items";
import classes from './Cart.module.css'


const Cart = props => {

    const [items, setItems] = useState([]);

    useEffect(() => {
        setItems([...items, props.cartItems])
    }, [props.cartItems]);
    // console.log(items);

    let [first, ...cartItems] = items
    console.log('cartItems',cartItems);

    let [totalPrice, setTotalPrice] = useState(0);
    useEffect(() => {
        setTotalPrice(totalPrice += props.cartItems.price)
    }, [props.cartItems])


    // console.log(totalPrice);


    return (<>
        <h1>CART</h1>
        <ul className={classes.cartList}>
            {cartItems.map(item => {
                return (<li className={classes.listNodes} key={Math.random()}>
                    <div className={classes.cartImage}>
                        <img src={cartItem.imageURL}></img>
                    </div>
                    <div>
                        <h2 className={classes.itemName}>{item.name}</h2>

                        <div className={classes.itemDetails}>
                            <div>{item.price}</div>
                            <div>{item.Quantity}  {item.measurment}</div>
                        </div>
                    </div>

                </li>)
            })
            }
        </ul>

        <div className={classes.totalPrice}>
            <h1>Total Price</h1>
            <h3>{totalPrice}</h3>
        </div>
    </>
    )

}

export default Cart;
